#!/bin/bash

# Script para verificar la persistencia de datos en la base de datos
# Este script ayuda a diagnosticar problemas de pรฉrdida de datos

set -e

echo "๐ Verificando persistencia de datos en PostgreSQL..."
echo ""

# Colores para output
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
RED='\033[0;31m'
NC='\033[0m' # No Color

# Verificar que Docker estรฉ corriendo
if ! docker ps > /dev/null 2>&1; then
    echo -e "${RED}โ Docker no estรก corriendo${NC}"
    exit 1
fi

# Verificar que el contenedor de PostgreSQL estรฉ corriendo
if ! docker ps | grep -q sigq-postgres; then
    echo -e "${YELLOW}โ๏ธ  El contenedor sigq-postgres no estรก corriendo${NC}"
    echo "Inicia los servicios con: cd docker && docker compose up -d postgres"
    exit 1
fi

echo -e "${GREEN}โ PostgreSQL estรก corriendo${NC}"
echo ""

# Verificar el volumen de datos
echo "๐ฆ Verificando volumen de datos..."
VOLUME_EXISTS=$(docker volume ls | grep -c postgres_data || true)
if [ "$VOLUME_EXISTS" -eq 0 ]; then
    echo -e "${YELLOW}โ๏ธ  El volumen postgres_data no existe${NC}"
    echo "Esto puede causar pรฉrdida de datos al reiniciar el contenedor"
else
    echo -e "${GREEN}โ Volumen postgres_data existe${NC}"
fi
echo ""

# Verificar tablas y datos
echo "๐ Verificando tablas y datos..."
echo ""

# Funciรณn para ejecutar consultas SQL
run_query() {
    docker compose -f ../docker/docker-compose.yml exec -T postgres psql -U sigq_user -d sigq_db -c "$1" 2>/dev/null || echo "Error"
}

# Verificar si la tabla surgeries existe
SURGERIES_EXISTS=$(run_query "SELECT EXISTS (SELECT FROM information_schema.tables WHERE table_name = 'surgeries');" | grep -c "t" || echo "0")

if [ "$SURGERIES_EXISTS" -eq 0 ]; then
    echo -e "${YELLOW}โ๏ธ  La tabla 'surgeries' no existe${NC}"
    echo "Necesitas crear las tablas primero:"
    echo "  1. Activa DB_SYNCHRONIZE=true en tu .env"
    echo "  2. Reinicia el backend"
    echo "  3. Luego cambia DB_SYNCHRONIZE=false"
else
    echo -e "${GREEN}โ La tabla 'surgeries' existe${NC}"
    
    # Contar cirugรญas
    SURGERY_COUNT=$(run_query "SELECT COUNT(*) FROM surgeries;" | grep -E "^[[:space:]]*[0-9]+" | tr -d ' ' || echo "0")
    echo "   ๐ Cirugรญas en la base de datos: $SURGERY_COUNT"
fi
echo ""

# Verificar otras tablas importantes
TABLES=("patients" "operating_rooms" "audit_logs")
for table in "${TABLES[@]}"; do
    EXISTS=$(run_query "SELECT EXISTS (SELECT FROM information_schema.tables WHERE table_name = '$table');" | grep -c "t" || echo "0")
    if [ "$EXISTS" -eq 1 ]; then
        COUNT=$(run_query "SELECT COUNT(*) FROM $table;" | grep -E "^[[:space:]]*[0-9]+" | tr -d ' ' || echo "0")
        echo -e "${GREEN}โ Tabla '$table': $COUNT registros${NC}"
    else
        echo -e "${YELLOW}โ๏ธ  Tabla '$table' no existe${NC}"
    fi
done
echo ""

# Verificar configuraciรณn de synchronize
echo "โ๏ธ  Verificando configuraciรณn..."
if [ -f "../docker/.env" ]; then
    if grep -q "DB_SYNCHRONIZE=true" ../docker/.env; then
        echo -e "${RED}โ๏ธ  DB_SYNCHRONIZE=true estรก activado${NC}"
        echo "Esto puede causar pรฉrdida de datos. Cambia a DB_SYNCHRONIZE=false"
    elif grep -q "DB_SYNCHRONIZE=false" ../docker/.env; then
        echo -e "${GREEN}โ DB_SYNCHRONIZE=false (configuraciรณn segura)${NC}"
    else
        echo -e "${YELLOW}โ๏ธ  DB_SYNCHRONIZE no estรก configurado${NC}"
        echo "Agrega DB_SYNCHRONIZE=false a tu .env"
    fi
else
    echo -e "${YELLOW}โ๏ธ  No se encontrรณ archivo .env${NC}"
fi
echo ""

# Resumen y recomendaciones
